#ifndef __CP15_INT_H__
#define __CP15_INT_H__
#if defined(__CR4__)
/****************************************************************************
 *  Inline assembly macros
 ****************************************************************************/
// Read ARM performance counter cycle count value
#if defined(__RVCT__)
#define CP15_PMU_GET_CYCLE_CNT(ret) \
    do { \
        __asm{MRC p15,0,ret,c9,c13,0} \
    } while(0)
#elif defined(__GNUC__)    /* __RVCT__ */
#define CP15_PMU_GET_CYCLE_CNT(ret) \
    do { \
        __asm__ __volatile__ ( \
            "MRC p15,0,%0,c9,c13,0\n\t" \
            : "=r" (ret) \
            : \
        ); \
    } while(0)
#else    /* __RVCT__ */
// MoDIS

#endif    /* __RVCT__ */

#endif    /* __CR4__ */
#endif    /* __CP15_INT_H__ */
